package Week3Day2;

public class FindCharCount {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String p ="Prathibha";
		int count=0;
		for(int i=0;i<=p.length()-1;i++) {
			if(p.charAt(i)=='a') {
				count ++;
		}
			
			

	}
		
		System.out.println("The occurence of a in the String "  +p+""+count);
	}}


